import { gsap } from "gsap";
import react, { useEffect, useState } from "react";

/* const wrapper = document.querySelectorAll(".nav-bottom"); */
/* const swipe = document.getElementsByClassName("swipe"); */

const NavBottom = () => {
  const [animation, setAnimation] = useState();

  useEffect(() => {
    document.querySelectorAll(".nav-bottom").forEach(data => {
      data.addEventListener("click", function(e) {
        e.preventDefault();
        if (e.currentTarget.innerText === "Contact")
          setAnimation(gsap.to(".pic", 1.5, { x: -1900 }));
        setAnimation(gsap.to(".contact-wrapper", 3, { x: 500, delay: -1 }));
      });
    });
  }, []);

  /* wrapper.forEach(data => {
      data.addEventListener("click", function(e) {
        if (e.currentTarget.innerText === "Contact") {
          console.log(e.currentTarget);
          gsap.to(swipe, 1, { x: -700 });
        }
        if (e.currentTarget.innerText === "About") {
          console.log(e.currentTarget);
          gsap.to(swipe, 1, { y: "-700" });
        } else {
          console.log(e.currentTarget);
          gsap.to(swipe, 1, { x: 700 });
        } 
      });*/
  /*  });
  }); */

  /*   
      
    }
     */
  /*  const titleBackGround = document.querySelector(".title");
    const zoom = document.querySelector("#btn");
    const img = document.querySelector(".image-container"); */

  /*  tl.from(letter, 2, { y: -200, ease: "back-out(1.7)", stagger: 0.25 }, 0, 3)
      .from(img, 1, { x: "120%", delay: -0.7 })
      .to(img, 1, { scale: 1.3, delay: -0.3 }); */

  return (
    <div className="nav-links-bottom">
      <a className="nav-bottom" href="">
        Skills
      </a>
      <a className="nav-bottom" href="">
        About
      </a>
      <a className="nav-bottom" href="">
        Contact
      </a>
      <style jsx>{`
        a,
        a:link {
          text-decoration: none;
          color: gray;
        }
        .nav-links-bottom {
          display: flex;
          justify-content: space-around;
          margin-top: 20px;
        }

        .nav-links-bottom a {
          color: rgb(155, 152, 152);
          opacity: 0.5;
          position: relative;
        }

        .nav-links-bottom a:before {
          content: "";
          position: absolute;
          width: 0;
          height: 2px;
          bottom: 0;
          right: 0;
          background-color: #fff;
          visibility: hidden;
          transition: all 0.3s ease-in-out;
        }

        .nav-links-bottom a:hover:before {
          visibility: visible;
          width: 100%;
        }

        .nav-links-bottom a:hover {
          transform: scale(1.3);
          opacity: 1;
        }
      `}</style>
    </div>
  );
};

export default NavBottom;
