import gsap from "gsap";
import react, { useEffect, useState } from "react";

const navHeader = () => {
  const [animation, setAnimation] = useState();
  const tl = gsap.timeline();

  useEffect(() => {
    const navTop = document.querySelectorAll(".nav-top");

    navTop[1].addEventListener("click", function(e) {
      e.preventDefault();
      tl.reverse();
      document.querySelectorAll(".nav-bottom").forEach(data => {
        data.addEventListener("click", function(e) {
          e.preventDefault();
          if (e.currentTarget.innerText === "Contact") {
            tl.play();
            setAnimation(gsap.to(".pics", 2.5, { x: -1900 }));
            setAnimation(gsap.to(".nav-bottom", 2.5, { x: -1900 }));
            setAnimation(
              gsap.to(".contact-wrapper", 1, {
                x: 230,
                delay: 0.5,
                display: "block"
              })
            );
          }
          if (e.currentTarget.innerText === "About") {
            tl.play();
            setAnimation(gsap.to(".pics", 3, { y: -1900, opacity: 0 }));
            setAnimation(gsap.to(".nav-bottom", 3, { y: -1900, opacity: 0 }));
            setAnimation(
              tl
                .to(".about-wrapper", 1, {
                  y: -340,
                  delay: 0.5,
                  display: "block"
                })
                .to(".intro-header", 3, { x: 0, delay: -10 })
            );
          }
          if (e.currentTarget.innerText === "Skills") {
            tl.play();
            setAnimation(gsap.to(".pics", 3, { y: -1900 }));
            setAnimation(gsap.to(".nav-bottom", 3, { y: -1900 }));
            setAnimation(
              gsap.to(".skills-wrapper", 1, {
                x: 235,
                delay: 0.5
              })
            );
          }
        });
      });
    });
  });
  return (
    <div className="nav-header">
      <div className="nav-name">Labidi.</div>
      <span className="space-between"></span>
      <div className="nav-links-top">
        <div>
          <a className="nav-top" href="/landingpage">
            Home
          </a>
        </div>
        <span className="space"></span>
        <a className="nav-top" href="#">
          Back
        </a>
      </div>
      <style jsx>{`
        a,
        a:link {
          text-decoration: none;
          color: rgb(155, 152, 152);
        }
        .nav-header {
          display: flex;
          justify-content: space-evenly;
          align-items: baseline;
          font-size: 20px;
        }
        .nav-name {
          color: white;
          font-size: 30px;
        }
        .nav-links-top {
          display: flex;
        }
        .nav-links-top a {
          color: rgb(155, 152, 152);
          opacity: 0.5;
          position: relative;
          padding: 0;
          margin: 0;
        }
        .nav-links-top a:before {
          content: "";
          position: absolute;
          width: 0;
          height: 2px;
          bottom: 0;
          right: 0;
          background-color: #fff;
          visibility: hidden;
          transition: all 0.3s ease-in-out;
        }
        .nav-links-top a:hover:before {
          visibility: visible;
          width: 100%;
        }
        .nav-links-top a:hover {
          transform: scale(1.3);
          opacity: 1;
        }
        .space-between {
          width: 50%;
        }
        .space {
          width: 30px;
        }
      `}</style>
    </div>
  );
};

export default navHeader;
