import react, { useEffect } from "react";
import { gsap } from "gsap";

const Pics = () => {
  const tl = gsap.timeline();

  useEffect(() => {
    const letter = document.querySelectorAll(".letter-title");
    const titleBackGround = document.querySelector(".title");
    const zoom = document.querySelector("#btn");
    const img = document.querySelector(".image-container");

    tl.from(titleBackGround, 2, { x: -"200%", ease: "sine-out" });
    tl.from(letter, 2, { y: -200, ease: "back-out(1.7)", stagger: 0.25 }, 0, 3)
      .from(img, 1, { x: "120%", delay: -0.7 })
      .to(img, 1, { scale: 1.3, delay: -0.3 });
  });

  return (
    <div className="swipe">
      <div className="pic">
        <div className="image-wrapper">
          <div className="overlay"></div>
          <img src="/skills.png" alt="Skills"></img>
        </div>
        <div className="image-wrapper">
          <div className="overlay"></div>
          <img src="/ali.png" alt="About"></img>
        </div>
        <div className="image-wrapper">
          <div className="overlay"></div>
          <img src="/contact.png" alt="Contact"></img>
        </div>
      </div>
      <style jsx>{`
        img {
          margin-top: 60px;
          width: 250px;
          height: 250px;
          filter: grayscale(100%);
        }
        .pic {
          display: flex;
          justify-content: space-around;
          margin-top: 20px;
        }
        .overlay {
          width: 100%;
          height: 100%;
          position: absolute;
          background-color: rgb(128, 125, 125);
          top: 100%;
        }
        .image-wrapper {
          position: relative;
          margin: 10px;
          overflow: hidden;
        }
      `}</style>
    </div>
  );
};
