import gsap from "gsap";
import react, { useEffect, useState } from "react";
import { Timeline } from "gsap";

const navHeader = () => {
  const [animation, setAnimation] = useState();

  useEffect(() => {
    const navTop = document.querySelectorAll(".nav-top");
    const tl = gsap.timeline();

    /* 
    navTop.forEach(data => {
      data.addEventListener("click", function(e) {
        if (e.currentTarget.innerText === "Back") {
          e.preventDefault();
          setAnimation(tl.reserved());
        }
      });
    }, []); */
    const click = e => {
      e.preventDefault();
      if (tl.play()) {
        tl.reversed();
      }
    };
  });
  return (
    <div className="nav-header">
      <div className="nav-name">Labidi.</div>
      <div className="nav-links-top">
        <a className="nav-top" href="">
          Home
        </a>
        <span className="space"></span>
        <a className="nav-top" onClick={click} href="">
          Back
        </a>
      </div>
      <style jsx>{`
        a,
        a:link {
          text-decoration: none;
          color: rgb(155, 152, 152);
        }
        .nav-name {
          font-size: 40px;
          margin-left: 100px;
          color: gray;
        }
        .nav-links-top {
          transform: translate(0%, -80%);
          font-size: 20px;
          margin-right: 100px;
          display: flex;
          justify-content: flex-end;
        }
        .nav-links-top a {
          color: rgb(155, 152, 152);
          opacity: 0.5;
          position: relative;
          padding: 0;
          margin: 0;
        }
        .nav-links-top a:before {
          content: "";
          position: absolute;
          width: 0;
          height: 2px;
          bottom: 0;
          right: 0;
          background-color: #fff;
          visibility: hidden;
          transition: all 0.3s ease-in-out;
        }
        .nav-links-top a:hover:before {
          visibility: visible;
          width: 100%;
        }
        .nav-links-top a:hover {
          transform: scale(1.3);
          opacity: 1;
        }
        .space {
          margin-left: 20px;
        }
      `}</style>
    </div>
  );
};

export default navHeader;
